package com.controlStst;

public class Pattern6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int n=3,i,j;
         for(i=0;i<n;i++) {
        	 for(j=0;j<i+1;j++){
        		 System.out.print(n-i);
        	 }
        	 System.out.println();
         }
	}

}
